mapper.update:
update register set roomstate="空闲"
where
roomNum=#{roomNum}

mapper.select:
select roomNum from register where reserve!=0 and roomNum in

	#{roomNum}

and
(
intime between #{inTime} and #{outTime}
or
outTime between #{inTime}
and #{outTime}
or
#{inTime} between inTime and
outTime
)
